const menuBtn=document.querySelector(".header__menu-btn"),mobileMenu=document.querySelector(".header__menu-list"),openMenuSvg=document.querySelector(".header__open-menu-svg"),closeMenuSvg=document.querySelector(".header__close-menu-svg");let menuStatus=!1;menuBtn.addEventListener("click",e=>{menuStatus?(menuStatus=!1,mobileMenu.classList.add("none"),openMenuSvg.classList.remove("none"),closeMenuSvg.classList.add("none")):(menuStatus=!0,mobileMenu.classList.remove("none"),openMenuSvg.classList.add("none"),closeMenuSvg.classList.remove("none"))}),document.addEventListener("click",e=>{e.target!==mobileMenu&&e.target!==menuBtn&&e.target!==openMenuSvg&&(menuStatus=!1,mobileMenu.classList.add("none"),openMenuSvg.classList.remove("none"),closeMenuSvg.classList.add("none"))});
